mapper.update:
UPDATE products
SET stock = stock - #{quantity}
WHERE no = #{pNo} AND stock >= #{quantity}

UPDATE products
SET stock = stock + #{quantity}
WHERE no = #{pNo}

UPDATE products
SET stock = #{stock}
WHERE no = #{pNo}

mapper.select:
SELECT stock
FROM products
WHERE no = #{pNo}

SELECT *
FROM products
WHERE c_no = #{cNo}
ORDER BY no ASC
LIMIT #{index}, #{size}
