mapper.select:
SELECT COALESCE(SUM(TIMESTAMPDIFF(MINUTE, start_time, end_time)), 0)
FROM seats_reservations
WHERE u_no = #{userNo}

SELECT COUNT(*)
FROM seats_reservations
WHERE u_no = #{userNo}

mapper.update:

UPDATE seats_reservations
SET end_time = #{now}
WHERE u_no = #{uNo}
AND end_time > NOW()
